mapper.delete:
delete
from ZT_COMPANY
where ID = #{id,jdbcType=VARCHAR}
  and DATA_VERSION = #{dataVersion,jdbcType=INTEGER}

mapper.update:
update ZT_COMPANY
set COMPANY_CODE = #{companyCode,jdbcType=VARCHAR},
    COMPANY_NAME = #{companyName,jdbcType=VARCHAR},
    MARKET       = #{market,jdbcType=INTEGER},
    PE           = #{pe,jdbcType=BIGINT},
    PB           = #{pb,jdbcType=BIGINT},
    TOTAL_CAP    = #{totalCap,jdbcType=BIGINT},
    FLOATING_CAP = #{floatingCap,jdbcType=BIGINT}
where ID = #{id,jdbcType=BIGINT}
  AND DATA_VERSION = #{dataVersion,jdbcType=INTEGER}
