mapper.insert:
insert into ZT_DATA_CATEGORY (DATA_CATEGORY_CODE, DATA_CATEGORY_NAME, 
  DATA_ROWS, DATA_UPDATED_AT, DATA_UPDATED_BY
  )
values (#{dataCategoryCode,jdbcType=VARCHAR}, #{dataCategoryName,jdbcType=VARCHAR}, 
  #{dataRows,jdbcType=BIGINT}, #{dataUpdatedAt,jdbcType=BIGINT}, #{dataUpdatedBy,jdbcType=BIGINT}
  )

mapper.update:
update ZT_DATA_CATEGORY
set DATA_CATEGORY_NAME = #{dataCategoryName,jdbcType=VARCHAR},
  DATA_ROWS = #{dataRows,jdbcType=BIGINT},
  DATA_UPDATED_AT = #{dataUpdatedAt,jdbcType=BIGINT},
  DATA_UPDATED_BY = #{dataUpdatedBy,jdbcType=BIGINT}
where DATA_CATEGORY_CODE = #{dataCategoryCode,jdbcType=VARCHAR}

mapper.select:
select DATA_CATEGORY_CODE, DATA_CATEGORY_NAME, DATA_ROWS, DATA_UPDATED_AT, DATA_UPDATED_BY
from ZT_DATA_CATEGORY
where DATA_CATEGORY_CODE = #{dataCategoryCode,jdbcType=VARCHAR}
